//密文
 
$crypttext = '';
 
$aeskey = base64_decode('qZe60QZFxuirub2ey4+7+Q==');
 
//AES解密，采用ECB模式
 
$aes = new Crypt_AES(CRYPT_MODE_ECB);
 
//设置AES密钥
 
$aes->setKey($aeskey);
 
//解密AES密文
 
$plaintext = $aes->decrypt(base64_decode($crypttext));
 
echo $plaintext;
 
echo '<hr />';
 
